options.customCss:
:root {
  /* Evio Production Brand Colors */
  --evio-primary: #1e40af;
  --evio-secondary: #f59e0b;
  --evio-accent: #10b981;
  --evio-dark: #1f2937;
  --evio-light: #f8fafc;
  --evio-error: #ef4444;
  --evio-success: #10b981;

  /* Production Typography */
  --font-family: 'Inter', 'Segoe UI', 'Roboto', sans-serif;
  --font-weight-normal: 400;
  --font-weight-medium: 500;
  --font-weight-semibold: 600;
  --font-weight-bold: 700;

  /* Production Layout */
  --container-width: 650px;
  --border-radius: 12px;
  --border-radius-small: 6px;
  --box-shadow: 0 10px 40px rgba(0, 0, 0, 0.12);
  --transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

* {
  box-sizing: border-box;
}

body {
  margin: 0;
  padding: 0;
  font-family: var(--font-family);
  background: linear-gradient(135deg, var(--evio-primary) 0%, var(--evio-secondary) 100%);
  min-height: 100vh;
}

.form-container {
  padding: 2rem 1rem;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
}

.form-card {
  background: white;
  border-radius: var(--border-radius);
  box-shadow: var(--box-shadow);
  padding: 3rem;
  margin: 0 auto;
  max-width: var(--container-width);
  width: 100%;
  position: relative;
  overflow: hidden;
}

.form-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, var(--evio-primary), var(--evio-accent));
}

.form-title {
  color: var(--evio-primary);
  font-size: 2.25rem;
  font-weight: var(--font-weight-bold);
  text-align: center;
  margin-bottom: 1rem;
  line-height: 1.2;
}

.form-description {
  color: #6b7280;
  text-align: center;
  margin-bottom: 2.5rem;
  line-height: 1.6;
  font-size: 1.1rem;
}

.form-field {
  margin-bottom: 1.5rem;
}

.form-field label {
  display: block;
  margin-bottom: 0.5rem;
  font-weight: var(--font-weight-medium);
  color: var(--evio-dark);
}

.form-field input,
.form-field select,
.form-field textarea {
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e5e7eb;
  border-radius: var(--border-radius-small);
  font-size: 1rem;
  transition: var(--transition);
  font-family: var(--font-family);
}

.form-field input:focus,
.form-field select:focus,
.form-field textarea:focus {
  outline: none;
  border-color: var(--evio-primary);
  box-shadow: 0 0 0 3px rgba(30, 64, 175, 0.1);
}

.form-field input:invalid {
  border-color: var(--evio-error);
}

.submit-button {
  background: linear-gradient(135deg, var(--evio-primary), var(--evio-accent));
  color: white;
  border: none;
  padding: 16px 32px;
  border-radius: var(--border-radius-small);
  font-size: 1.1rem;
  font-weight: var(--font-weight-semibold);
  cursor: pointer;
  transition: var(--transition);
  width: 100%;
  margin-top: 1rem;
}

.submit-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(30, 64, 175, 0.3);
}

.privacy-notice {
  background: #f8f9fa;
  border-left: 4px solid var(--evio-accent);
  padding: 1rem;
  margin-top: 2rem;
  border-radius: var(--border-radius-small);
  font-size: 0.9rem;
  color: #6b7280;
}

@media (max-width: 768px) {
  .form-container {
    padding: 1rem;
  }

  .form-card {
    padding: 2rem;
  }

  .form-title {
    font-size: 1.875rem;
  }
}

